query:
query {
        Catalog {
            catalogOffers(
                namespace: "epic",
                locale: "es-ES",
                params: {
                    category: "freegames",
                    country: "AR",
                    sortBy: "effectiveDate",
                    sortDir: "asc"
                }) {
                elements {
                    title
                    keyImages {
                        type
                        url
                    }
                    productSlug
                    promotions {
                        promotionalOffers {
                            promotionalOffers {
                                startDate
                                endDate
                                discountSetting {
                                    discountType
                                    discountPercentage
                                }
                            }
                        }
                        upcomingPromotionalOffers {
                            promotionalOffers {
                                startDate
                                endDate
                                discountSetting {
                                    discountType
                                    discountPercentage
                                }
                            }
                        }
                    }
                }
            }
        }
}
